CREATE OR REPLACE PROCEDURE qua_gerar_pessoas_tre_grupo ( nr_seq_treinamento_p bigint, cd_lista_grupo_p text, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
cd_setor_usuario_w	bigint;

C01 CURSOR FOR
	SELECT	distinct
		b.cd_pessoa_fisica,
		b.cd_setor_atendimento
	from    	usuario b,
		usuario_grupo a
	where   	a.nm_usuario_grupo= b.nm_usuario
	and     	b.ie_situacao     = 'A'
	and     	a.ie_situacao     = 'A'
	and	(b.cd_pessoa_fisica IS NOT NULL AND b.cd_pessoa_fisica::text <> '')
	and	obter_se_contido(a.nr_seq_grupo, cd_lista_grupo_p) = 'S'
	and not exists (	SELECT	1
			from	qua_doc_trein_pessoa x
			where	x.cd_pessoa_fisica		= b.cd_pessoa_fisica
			and	x.nr_seq_treinamento	= nr_seq_treinamento_p);


BEGIN

if (coalesce(cd_lista_grupo_p,'X') <> 'X') then
	begin
	open C01;
	loop
	fetch C01 into
		cd_pessoa_fisica_w,
		cd_setor_usuario_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		insert into qua_doc_trein_pessoa(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				nr_seq_treinamento,
				cd_pessoa_fisica,
				ie_faltou,
				cd_setor_atendimento)
			values (	nextval('qua_doc_trein_pessoa_seq'),
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_treinamento_p,
				cd_pessoa_fisica_w,
				'N',
				cd_setor_usuario_w);
		end;
	end loop;
	close C01;

	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
