CREATE OR REPLACE PROCEDURE qua_gerar_pessoas_tre_perfil ( nr_seq_treinamento_p bigint, cd_perfil_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w		varchar(10);
cd_setor_atendimento_w		bigint;

C01 CURSOR FOR
	SELECT	distinct
		a.cd_pessoa_fisica,
		a.cd_setor_atendimento
	from	usuario a,
		usuario_perfil b
	where	a.ie_situacao	= 'A'
	and	b.nm_usuario	= a.nm_usuario
	and	(a.cd_pessoa_fisica IS NOT NULL AND a.cd_pessoa_fisica::text <> '')
	and	b.cd_perfil	= cd_perfil_p
	and	not exists (	SELECT	1
				from	qua_doc_trein_pessoa x
				where	x.cd_pessoa_fisica		= a.cd_pessoa_fisica
				and	x.nr_seq_treinamento	= nr_seq_treinamento_p);


BEGIN

open C01;
loop
fetch C01 into
	cd_pessoa_fisica_w,
	cd_setor_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	insert into qua_doc_trein_pessoa(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			nr_seq_treinamento,
			cd_pessoa_fisica,
			ie_faltou,
			cd_setor_atendimento)
		values (	nextval('qua_doc_trein_pessoa_seq'),
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_treinamento_p,
			cd_pessoa_fisica_w,
			'N',
			cd_setor_atendimento_w);
	end;
end loop;
close C01;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
