CREATE OR REPLACE PROCEDURE qua_gerar_processo_documento ( nr_sequencia_p bigint, nr_seq_processo_p bigint, nr_seq_subprocesso_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w			bigint;


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then
	begin
	if (coalesce(nr_seq_processo_p,0) > 0) then
		begin
		select	count(*)
		into STRICT	qt_existe_w
		from	qua_doc_proc_emp
		where	nr_seq_proc = coalesce(nr_seq_processo_p,0)
		and	nr_seq_doc = nr_sequencia_p;

		if (qt_existe_w = 0) then
			insert into qua_doc_proc_emp(
				nr_sequencia,
				nr_seq_doc,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_proc)
			values (	nextval('qua_doc_proc_emp_seq'),
				nr_sequencia_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_processo_p);
		end if;
		end;
	end if;

	if (coalesce(nr_seq_subprocesso_p,0) > 0) then
		insert into qua_doc_subproc_emp(
				nr_sequencia,
				nr_seq_subprocesso,
				nr_seq_doc,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec)
			values (	nextval('qua_doc_subproc_emp_seq'),
				nr_seq_subprocesso_p,
				nr_sequencia_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p);
	end if;

	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
