SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE qua_gerar_questao_evento ( nr_sequencia_p bigint, nr_seq_tipo_analise_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w				bigint;
nr_seq_evento_w				bigint;
nr_seq_tipo_analise_w			bigint;
nr_seq_questao_w				bigint;
nr_seq_apres_w				bigint;

/* Campos pré-cadastrados no cadastro (Tipos de análise da qualidade) */

ds_condicoes_clinicas_w			varchar(2000);
ds_causas_w				varchar(2000);
ds_consequencias_w			varchar(2000);
ds_conduta_pos_evento_w			varchar(2000);

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.nr_seq_apres,
		a.ds_condicoes_clinicas,
		a.ds_causas,
		a.ds_consequencias,
		a.ds_conduta_pos_evento
	from	qua_questao_analise a,
		qua_grupo_analise b
	where	a.nr_seq_grupo = b.nr_sequencia
	and	b.nr_seq_tipo = nr_seq_tipo_analise_w
	and	coalesce(a.ie_situacao,'A') = 'A'
	and	coalesce(b.ie_situacao,'A') = 'A';


BEGIN
select	coalesce(b.nr_seq_tipo_analise,nr_seq_tipo_analise_p)
into STRICT	nr_seq_tipo_analise_w
from	qua_evento b,
	qua_evento_paciente a
where	a.nr_sequencia  = nr_sequencia_p
and	a.nr_seq_evento = b.nr_sequencia;

OPEN C01;
LOOP
FETCH C01 INTO
	nr_seq_questao_w,
	nr_seq_apres_w,
	ds_condicoes_clinicas_w,
	ds_causas_w,
	ds_consequencias_w,
	ds_conduta_pos_evento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	nextval('qua_evento_pac_questao_seq')
	into STRICT	nr_sequencia_w
	;

	insert	into qua_evento_pac_questao(
		nr_sequencia,
		nr_seq_evento,
		nr_seq_questao,
		dt_atualizacao,
		nm_usuario,
		nr_seq_apres,
		ds_condicoes_clinicas,
		ds_causas,
		ds_consequencias,
		ds_conduta_pos_evento)
	values (	nr_sequencia_w,
		nr_sequencia_p,
		nr_seq_questao_w,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_apres_w,
		ds_condicoes_clinicas_w,
		ds_causas_w,
		ds_consequencias_w,
		ds_conduta_pos_evento_w);
	end;
END LOOP;
CLOSE C01;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
