CREATE OR REPLACE PROCEDURE qua_gerar_setor_envolv_doc ( nr_seq_documento_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_seq_tipo_w			bigint;
cd_setor_atendimento_w		integer;
cd_estab_w			smallint := wheb_usuario_pck.get_cd_estabelecimento;
qt_existe_w			bigint;

C01 CURSOR FOR
SELECT	cd_setor_atendimento
from	qua_tipo_doc_setor_padrao
where	nr_seq_tipo_doc = nr_seq_tipo_w
and	coalesce(cd_estabelecimento_lib,cd_estab_w) = cd_estab_w;


BEGIN

select	nr_seq_tipo
into STRICT	nr_seq_tipo_w
from	qua_documento
where	nr_sequencia = nr_seq_documento_p;

open C01;
loop
fetch C01 into
	cd_setor_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_existe_w
	from	qua_doc_setor_adic
	where	nr_seq_doc = nr_seq_documento_p
	and	cd_setor_atendimento = cd_setor_atendimento_w;

	if (qt_existe_w = 0) then
		begin

		insert into qua_doc_setor_adic(
			nr_sequencia,
			nr_seq_doc,
			dt_atualizacao,
			nm_usuario,
			cd_setor_atendimento,
			dt_atualizacao_nrec,
			nm_usuario_nrec)
		values (	nextval('qua_doc_setor_adic_seq'),
			nr_seq_documento_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_setor_atendimento_w,
			clock_timestamp(),
			nm_usuario_p);

		end;
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
