CREATE OR REPLACE PROCEDURE qua_gravar_log_consulta ( nr_sequencia_p bigint, nm_usuario_p text, ie_log_p text) AS $body$
DECLARE


nr_seq_doc_inferior_w		bigint;
nr_seq_doc_superior_w		bigint;
ie_doc_pai_w				varchar(1);

C01 CURSOR FOR
SELECT	nr_sequencia
from	qua_documento
where	nr_seq_superior = nr_seq_doc_superior_w;


BEGIN

/*
N√ÉO UTILIZAR - Utilizar qua_gravar_doc_log_acesso

*/
insert	into qua_doc_log_acesso(
	nr_sequencia,
	nr_seq_doc,
	dt_atualizacao,
	nm_usuario,
	dt_acesso,
	ie_log,
	dt_leitura)
values (nextval('qua_doc_log_acesso_seq'),
	nr_sequencia_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	ie_log_p,
	clock_timestamp());

select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
into STRICT	ie_doc_pai_w
from	qua_documento
where	nr_sequencia = nr_sequencia_p
and		coalesce(nr_seq_superior::text, '') = '';

if (ie_doc_pai_w = 'S') then
	nr_seq_doc_superior_w := nr_sequencia_p;
else
begin

	select	max(nr_seq_superior)
	into STRICT 	nr_seq_doc_superior_w
	from	qua_documento
	where	nr_sequencia = nr_sequencia_p;

	insert	into qua_doc_log_acesso(
				nr_sequencia,
				nr_seq_doc,
				dt_atualizacao,
				nm_usuario,
				dt_acesso,
				ie_log,
				dt_leitura)
	values (
				nextval('qua_doc_log_acesso_seq'),
				nr_seq_doc_superior_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				ie_log_p,
				clock_timestamp());

end;
end if;

open C01;
loop
fetch C01 into
	nr_seq_doc_inferior_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
begin

	insert	into qua_doc_log_acesso(
				nr_sequencia,
				nr_seq_doc,
				dt_atualizacao,
				nm_usuario,
				dt_acesso,
				ie_log,
				dt_leitura)
	values (
				nextval('qua_doc_log_acesso_seq'),
				nr_seq_doc_inferior_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				ie_log_p,
				clock_timestamp());

end;

end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
