CREATE OR REPLACE PROCEDURE qua_inserir_func_mod ( nm_usuario_p text, nr_seq_modulo_p bigint, ds_lista_funcao_p text) AS $body$
DECLARE


ds_lista_funcao_w			varchar(2000);
tam_lista_w			bigint;
ie_pos_virgula_w			smallint	:= 0;
cd_funcao_w			integer;
nr_sequencia_w			bigint;


BEGIN
ds_lista_funcao_w	:= ds_lista_funcao_p;
if (ds_lista_funcao_w IS NOT NULL AND ds_lista_funcao_w::text <> '') then
	begin
	while(ds_lista_funcao_w IS NOT NULL AND ds_lista_funcao_w::text <> '') loop
		begin
		tam_lista_w	:=	length(ds_lista_funcao_w);
		ie_pos_virgula_w	:=	position(',' in ds_lista_funcao_w);
		if (ie_pos_virgula_w <> 0) then
			begin
			cd_funcao_w	:= substr(ds_lista_funcao_w,1,(ie_pos_virgula_w - 1));
			ds_lista_funcao_w	:= substr(ds_lista_funcao_w,(ie_pos_virgula_w + 1),tam_lista_w);
			end;
		end if;

		select	nextval('func_mod_software_seq')
		into STRICT	nr_sequencia_w
		;

		insert	into func_mod_software(
				cd_funcao,
				dt_atualizacao,
				dt_atualizacao_nrec,
				nm_usuario,
				nm_usuario_nrec,
				nr_seq_modulo,
				nr_sequencia)
			values ( 	cd_funcao_w,
				clock_timestamp(),
				clock_timestamp(),
				nm_usuario_p,
				nm_usuario_p,
				nr_seq_modulo_p,
				nr_sequencia_w);
		end;
	end loop;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
