CREATE OR REPLACE PROCEDURE qua_inserir_grupo_partic_ata ( nr_seq_ata_p bigint, nr_seq_grupo_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w		varchar(10);
cd_setor_atend_w		bigint;

c01 CURSOR FOR
SELECT	a.cd_pessoa_fisica
from	usuario a,
	usuario_grupo b
where	a.nm_usuario = b.nm_usuario_grupo
and	b.nr_seq_grupo = nr_seq_grupo_p
and	b.ie_situacao = 'A'
and	a.ie_situacao = 'A'
and	(a.cd_pessoa_fisica IS NOT NULL AND a.cd_pessoa_fisica::text <> '');


BEGIN

open c01;
loop
fetch c01 into
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	substr(obter_setor_usuario(obter_usuario_pessoa(cd_pessoa_fisica_w)),1,10)
	into STRICT	cd_setor_atend_w
	;

	insert into proj_ata_participante(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_ata,
		ie_faltou,
		cd_pessoa_participante,
		cd_setor_atendimento)
	values (nextval('proj_ata_participante_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_ata_p,
		'N',
		cd_pessoa_fisica_w,
		cd_setor_atend_w);

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
