CREATE OR REPLACE PROCEDURE qua_liberar_avaliacao ( nr_seq_avaliacao_p bigint, ie_opcao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Liberar/Estornar a avaliação.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN
if (nr_seq_avaliacao_p IS NOT NULL AND nr_seq_avaliacao_p::text <> '') then
	if (ie_opcao_p = 'L') then
		update	qua_avaliacao
		set	dt_liberacao 	= clock_timestamp(),
			nm_usuario   	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= nr_seq_avaliacao_p;
	elsif (ie_opcao_p = 'E') then
		update	qua_avaliacao
		set	dt_liberacao 	 = NULL,
			nm_usuario   	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= nr_seq_avaliacao_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
