CREATE OR REPLACE PROCEDURE qua_liberar_treinamento ( nr_seq_doc_trein_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_consiste_resp_partic_w		varchar(1)	:= 'N';
qt_existe_w			bigint;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type	:= wheb_usuario_pck.get_cd_estabelecimento;


BEGIN

ie_consiste_resp_partic_w := obter_param_usuario(4000, 309, obter_perfil_Ativo, nm_usuario_p, cd_estabelecimento_w, ie_consiste_resp_partic_w);

if (ie_consiste_resp_partic_w = 'S') then
	begin

	select	count(*)
	into STRICT	qt_existe_w
	from	qua_doc_trein_resp
	where	nr_seq_treinamento = nr_seq_doc_trein_p;

	if (qt_existe_w = 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266441);
	end if;

	select	count(*)
	into STRICT	qt_existe_w
	from	qua_doc_trein_pessoa
	where	nr_seq_treinamento = nr_seq_doc_trein_p;

	if (qt_existe_w = 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266444);
	end if;

	end;
end if;

update	qua_doc_treinamento
set	dt_liberacao	= clock_timestamp(),
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_doc_trein_p;

commit;

end;
$body$
