CREATE OR REPLACE PROCEDURE qua_lib_questionario ( nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN
update	qua_doc_questionario
set 	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp(),
	nm_usuario_libera = CASE WHEN ie_opcao_p='L' THEN  nm_usuario_p  ELSE null END ,
	dt_liberacao = CASE WHEN ie_opcao_p='L' THEN  clock_timestamp()  ELSE null END
where 	nr_sequencia = nr_sequencia_p;

commit;

end;
