CREATE OR REPLACE PROCEDURE qua_obter_dados_acesso_arquivo ( nm_user_ftp_p INOUT text, ds_senha_p INOUT text, ds_diretorio_p INOUT text, ds_host_p INOUT text, ds_diretorio_rede_p INOUT text, nr_porta_p INOUT bigint) AS $body$
BEGIN

select	nm_user_ftp,
	ds_senha,
	ds_diretorio,
	ds_host,
	ds_diretorio_rede,
	coalesce(nr_porta,21)
into STRICT	nm_user_ftp_p,
	ds_senha_p,
	ds_diretorio_p,
	ds_host_p,
	ds_diretorio_rede_p,
	nr_porta_p
from	sis_conexao_ftp
where	ie_ponto_sistema	= 'CQ';

end;
