CREATE OR REPLACE PROCEDURE qua_reabrir_nao_conform ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_pessoa_fisica_w	varchar(10);


BEGIN 
update	qua_nao_conformidade 
set	dt_encerramento		 = NULL, 
	ie_status			= 'Aberta', 
	nr_seq_motivo_encer	 = NULL, 
	nm_usuario		= nm_usuario_p, 
	dt_atualizacao		= clock_timestamp(), 
	nr_seq_motivo_subst	 = NULL, 
	nr_seq_motivo_cancel	 = NULL 
where	nr_sequencia		= nr_sequencia_p;
 
select	substr(obter_pessoa_fisica_usuario(nm_usuario_p, 'C'),1,10) 
into STRICT	cd_pessoa_fisica_w
;
 
insert into qua_nao_conform_hist( 
			nr_sequencia, 
			nr_seq_nao_conform, 
			dt_atualizacao, 
			nm_usuario, 
			ds_historico, 
			cd_pessoa_fisica, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			dt_historico, 
			ie_origem) 
		values (	nextval('qua_nao_conform_hist_seq'), 
			nr_sequencia_p, 
			clock_timestamp(), 
			'Tasy', 
			substr(wheb_mensagem_pck.get_texto(311599),1,100), 
			cd_pessoa_fisica_w, 
			clock_timestamp(), 
			'Tasy', 
			clock_timestamp(), 
			'S');
commit;
 
end;
