CREATE OR REPLACE PROCEDURE qua_registrar_analise_evento ( nr_sequencia_p bigint, ie_data_analise_p text, ie_concluir_evento_p text, ie_classificacao_p text, nm_usuario_p text) AS $body$
BEGIN

if (nr_sequencia_p	> 0) then
	update	qua_evento_paciente
	set	dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		dt_analise	= CASE WHEN ie_data_analise_p='S' THEN clock_timestamp()  ELSE dt_analise END ,
		nm_usuario_analise	= CASE WHEN ie_data_analise_p='S' THEN nm_usuario_p  ELSE nm_usuario_analise END ,
		ie_status		= CASE WHEN ie_concluir_evento_p='S' THEN '5'  ELSE ie_status END ,
		ie_classificacao	= CASE WHEN ie_classificacao_p='' THEN ie_classificacao  ELSE ie_classificacao_p END
	where	nr_sequencia 	= nr_sequencia_p;
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
