CREATE OR REPLACE PROCEDURE qua_replicar_partic_acesso_ata ( nr_seq_ata_p bigint, nm_usuario_p text) AS $body$
DECLARE


nm_usuario_lib_w	usuario.nm_usuario%type;
cd_setor_atendimento_w	proj_ata_participante.cd_setor_atendimento%type;
qt_registro_w		bigint;

c01 CURSOR FOR
	SELECT	obter_usuario_ativo_pf(cd_pessoa_participante),
		a.cd_setor_atendimento
	from	proj_ata_participante a
	where	nr_seq_ata = nr_seq_ata_p
	and ((a.cd_pessoa_participante IS NOT NULL AND a.cd_pessoa_participante::text <> '') or
		(a.cd_setor_atendimento IS NOT NULL AND a.cd_setor_atendimento::text <> ''));


BEGIN

if (coalesce(nr_seq_ata_p,0) > 0) then
	begin
	open C01;
	loop
	fetch C01 into
		nm_usuario_lib_w,
		cd_setor_atendimento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	count(*)
		into STRICT	qt_registro_w
		from	proj_ata_lib
		where	nr_seq_ata = nr_seq_ata_p
		and	((cd_setor_atendimento = cd_setor_atendimento_w)
			or (coalesce(cd_setor_atendimento::text, '') = '' and coalesce(cd_setor_atendimento_w::text, '') = ''))
		and	((nm_usuario_lib = nm_usuario_lib_w)
			or (coalesce(nm_usuario_lib::text, '') = '' and coalesce(nm_usuario_lib_w::text, '') = ''));

		if (qt_registro_w = 0) then
			begin
			insert into proj_ata_lib(
				nr_sequencia,
				nr_seq_ata,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				cd_setor_atendimento,
				cd_cargo,
				nr_seq_grupo_usuario,
				nm_usuario_lib)
			values (	nextval('proj_ata_lib_seq'),
				nr_seq_ata_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				cd_setor_atendimento_w,
				null,
				null,
				nm_usuario_lib_w);
			end;
		end if;
		end;
	end loop;
	close C01;

	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
