CREATE OR REPLACE PROCEDURE qua_replicar_partic_treina ( nr_seq_treina_origem_p bigint, nr_seq_treina_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w			bigint;

cd_pessoa_fisica_w		pessoa_fisica.cd_pessoa_fisica%type;
nm_pessoa_externo_w		qua_doc_trein_pessoa.nm_pessoa_externo%type;
cd_setor_atendimento_w		setor_atendimento.cd_setor_atendimento%type;

c01 CURSOR FOR
SELECT	a.cd_pessoa_fisica,
	a.nm_pessoa_externo,
	a.cd_setor_atendimento
from	qua_doc_trein_pessoa a
where	a.nr_seq_treinamento = nr_seq_treina_origem_p;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	qua_doc_treinamento
where	nr_sequencia = nr_seq_treina_origem_p;

if (qt_existe_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(267377,'NR_SEQ_TREINA=' || nr_seq_treina_origem_p);
end if;

if (nr_seq_treina_origem_p = nr_seq_treina_destino_p) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(267395);
end if;

open c01;
loop
fetch c01 into
	cd_pessoa_fisica_w,
	nm_pessoa_externo_w,
	cd_setor_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	count(*)
	into STRICT	qt_existe_w
	from	qua_doc_trein_pessoa a
	where	a.nr_seq_treinamento = nr_seq_treina_destino_p
	and	(((cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') and cd_pessoa_fisica = cd_pessoa_fisica_w) or ((nm_pessoa_externo_w IS NOT NULL AND nm_pessoa_externo_w::text <> '') and upper(nm_pessoa_externo) = upper(nm_pessoa_externo_w)));

	if (qt_existe_w = 0) then
		begin

		insert into qua_doc_trein_pessoa(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_treinamento,
			cd_pessoa_fisica,
			nm_pessoa_externo,
			cd_setor_atendimento,
			ie_faltou)
		values (
			nextval('qua_doc_trein_pessoa_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_treina_destino_p,
			cd_pessoa_fisica_w,
			nm_pessoa_externo_w,
			cd_setor_atendimento_w,
			'N');

		end;
	end if;

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
