CREATE OR REPLACE PROCEDURE qua_reprovar_documento ( nr_sequencia_p bigint, ds_motivo_p text, nm_usuario_p text) AS $body$
DECLARE


dt_validacao_w			qua_documento.dt_validacao%type;
dt_elaboracao_w			qua_documento.dt_elaboracao%type;
nr_seq_superior_w		bigint;
cd_pessoa_validacao_w		qua_documento.cd_pessoa_validacao%type;
cd_pessoa_aprov_w               qua_documento.cd_pessoa_aprov%type;

C01 CURSOR FOR
SELECT	nr_sequencia
from	qua_documento
where	nr_seq_superior = nr_sequencia_p;


BEGIN

begin
select	dt_validacao,
	dt_elaboracao,
	nr_seq_superior,
	cd_pessoa_validacao,
	cd_pessoa_aprov
into STRICT	dt_validacao_w,
	dt_elaboracao_w,
	nr_seq_superior_w,
	cd_pessoa_validacao_w,
	cd_pessoa_aprov_w
from	qua_documento
where	nr_sequencia = nr_sequencia_p;
exception
when others then
	nr_seq_superior_w := 0;
end;

open C01;
loop
fetch C01 into
	nr_seq_superior_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
begin
	if (nr_seq_superior_w > 0) then
	begin

		update	qua_documento
		set	dt_validacao	 = NULL,
			dt_elaboracao	 = NULL,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp(),
			dt_reprovacao	= clock_timestamp(),
			ie_status	= 'O'
		where	nr_sequencia	= nr_seq_superior_w;
	end;
	end if;
end;
end loop;
close C01;

update	qua_documento
set	dt_validacao	 = NULL,
	dt_elaboracao	 = NULL,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp(),
	dt_reprovacao	= clock_timestamp(),
	ie_status	= 'O'
where	nr_sequencia	= nr_sequencia_p;


insert into qua_doc_historico(
	nr_sequencia,
	nr_seq_documento,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	ds_historico)
values (nextval('qua_doc_historico_seq'),
	nr_sequencia_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	ds_motivo_p);
commit;
end;
$body$
