SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE qua_reprovar_revisao_doc ( nr_sequencia_p bigint, nm_usuario_p text, ds_motivo_p text default '') AS $body$
DECLARE

			 
nr_seq_doc_w			bigint;
cd_estabelecimento_w		smallint;
nr_seq_superior_w 		bigint;
nr_seq_rev_w 			bigint;
nr_seq_tipo_hist_w		bigint;
nm_user_w				varchar(60);
nr_seq_ordem_serv_w		bigint;
ds_relat_tecnico_w		man_ordem_serv_tecnico.ds_relat_tecnico%type;
nr_seq_localizacao_w	man_localizacao.nr_sequencia%type;
cd_cnpj_w				man_localizacao.cd_cnpj%type;
nr_seq_idioma_w			pessoa_juridica.nr_seq_idioma%type;

C01 CURSOR FOR 
SELECT	nr_sequencia 
from	qua_documento 
where	nr_seq_superior = nr_seq_doc_w;


BEGIN 
 
begin 
select	a.nr_sequencia, 
	a.cd_estabelecimento, 
	b.nr_seq_ordem_serv 
into STRICT	nr_seq_doc_w, 
	cd_estabelecimento_w, 
	nr_seq_ordem_serv_w 
from	qua_doc_revisao b, 
	qua_documento a 
where	a.nr_sequencia = b.nr_seq_doc 
and	b.nr_sequencia = nr_sequencia_p;
exception 
when others then 
	nr_seq_doc_w := 0;
end;
 
select	substr(user,1,4) 
into STRICT	nm_user_w
;
 
if (nr_seq_doc_w > 0) then 
begin 
 
open C01;
loop 
fetch C01 into 
	nr_seq_superior_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
begin 
	if (nr_seq_superior_w > 0) then 
	begin 
	 
		select	max(b.nr_sequencia) 
		into STRICT	nr_seq_rev_w 
		from	qua_doc_revisao b, 
			qua_documento a 
		where	a.nr_sequencia = b.nr_seq_doc 
		and	a.nr_sequencia = nr_seq_superior_w;
	 
	 
		update	qua_doc_revisao 
		set	dt_validacao	 = NULL, 
			dt_atualizacao	= clock_timestamp(), 
			nm_usuario	= nm_usuario_p, 
			ds_observacao	= substr(ds_observacao || ' ' || ds_motivo_p,1,2000) 
		where	nr_sequencia	= nr_seq_rev_w;
	 
	end;
	end if;
end;	
end loop;
close C01;
 
	update	qua_doc_revisao 
	set	dt_validacao	 = NULL, 
		dt_atualizacao	= clock_timestamp(), 
		nm_usuario	= nm_usuario_p, 
		ds_observacao	= substr(ds_observacao || ' ' || ds_motivo_p,1,2000) 
	where	nr_sequencia	= nr_sequencia_p;
	 
	CALL qua_gravar_doc_log_acesso(nr_seq_doc_w,'C',nm_usuario_p);
	CALL qua_gerar_envio_comunicacao(nr_seq_doc_w, '0', nm_usuario_p, '31', cd_estabelecimento_w, null, null, 'N');
	 
	if (nm_user_w = 'CORP') and (nr_seq_ordem_serv_w > 0) and (coalesce(ds_motivo_p,'X') <> 'X') then 
		begin 
		 
		 
		 
		select	nr_seq_localizacao 
		into STRICT	nr_seq_localizacao_w 
		from	man_ordem_servico 
		where	nr_sequencia = nr_seq_ordem_serv_w;
		 
		ds_relat_tecnico_w := wheb_mensagem_pck.get_texto(305757) /*'Revisão do documento reprovada. '*/
 || chr(13) || chr(10) || 
								wheb_mensagem_pck.get_texto(305761,'DS_MOTIVO_W='||ds_motivo_p); --'Motivo: ' || ds_motivo_p, 
		
		if (nr_seq_localizacao_w IS NOT NULL AND nr_seq_localizacao_w::text <> '') then 
			select	max(a.cd_cnpj) 
			into STRICT	cd_cnpj_w 
			from	man_localizacao	a 
			where	a.nr_sequencia	= nr_seq_localizacao_w;
			 
			if (cd_cnpj_w IS NOT NULL AND cd_cnpj_w::text <> '') then 
				select	max(a.nr_seq_idioma) 
				into STRICT	nr_seq_idioma_w 
				from	pessoa_juridica	a 
				where	a.cd_cgc	= cd_cnpj_w;
 
				if (nr_seq_idioma_w IS NOT NULL AND nr_seq_idioma_w::text <> '') then 
					ds_relat_tecnico_w	:= substr(obter_texto_dic_objeto(305757, nr_seq_idioma_w, null) /*'Revisão do documento reprovada. '*/
|| chr(13) || chr(10) || 
												 obter_texto_dic_objeto(305761, nr_seq_idioma_w, 'DS_MOTIVO_W='||ds_motivo_p)/*'Motivo: ' || ds_motivo_p,*/, 1, 255);
				end if;
			end if;
		end if;
		 
		 
		insert into man_ordem_serv_tecnico( 
				nr_sequencia, 
				nr_seq_ordem_serv, 
				dt_atualizacao, 
				nm_usuario, 
				ds_relat_tecnico, 
				dt_historico, 
				ie_origem, 
				nr_seq_tipo, 
				dt_liberacao, 
				nm_usuario_lib) 
			values (	nextval('man_ordem_serv_tecnico_seq'), 
				nr_seq_ordem_serv_w, 
				clock_timestamp(), 
				nm_usuario_p, 
				ds_relat_tecnico_w, 
				clock_timestamp(), 
				'I', 
				1, 
				clock_timestamp(), 
				nm_usuario_p);
		 
		end;
	end if;	
	commit;
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
