SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE qua_revalidacao_documento ( nr_sequencia_p bigint, ie_opcao_p text, dt_aprovacao_p timestamp, nm_usuario_p text) AS $body$
DECLARE

/*ie_opcao_p
R - Revalidar
E - Estornar revalidação
*/
dt_revalidacao_w	timestamp;


BEGIN

if (ie_opcao_p = 'E') then
	dt_revalidacao_w := null;
elsif (ie_opcao_p = 'R') then
	dt_revalidacao_w := coalesce(dt_aprovacao_p, clock_timestamp());
end if;

update	qua_documento
set	ie_status 			= 'D',
	dt_revalidacao 		= dt_revalidacao_w,
	nm_usuario_revalidacao 	= nm_usuario_p,
	ie_situacao 		= 'A'
where	nr_sequencia 		= nr_sequencia_p;

insert into	qua_doc_revalid_log(
	dt_atualizacao,
	dt_atualizacao_nrec,
	dt_revalidacao,
	nm_usuario,
	nm_usuario_nrec,
	nr_seq_documento,
	nr_sequencia)
values (	clock_timestamp(),
	clock_timestamp(),
	dt_revalidacao_w,
	nm_usuario_p,
	nm_usuario_p,
	nr_sequencia_p,
	nextval('qua_doc_revalid_log_seq'));
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
