CREATE OR REPLACE PROCEDURE qua_substituir_nao_conform ( nr_sequencia_p bigint, nr_seq_motivo_subst_p bigint, ds_motivo_subst_p text, ie_encerrar_nc_p text, nm_usuario_p text) AS $body$
DECLARE

 
ds_motivo_w		varchar(80);
ds_historico_w		varchar(4000);
ds_motivo_subst_w	varchar(4000);


BEGIN 
 
update	qua_nao_conformidade 
set	nr_seq_motivo_subst	= nr_seq_motivo_subst_p, 
	ie_status		= wheb_mensagem_pck.get_texto(347779), 
	dt_atualizacao		= clock_timestamp(), 
	nm_usuario		= nm_usuario_p 
where	nr_sequencia		= nr_sequencia_p;
 
if (ie_encerrar_nc_p = 'S') then 
	 
	update	qua_nao_conformidade 
	set	dt_encerramento	= clock_timestamp(), 
		ie_status	= wheb_mensagem_pck.get_texto(305767) 
	where	nr_sequencia	= nr_sequencia_p;
 
end if;
 
select	substr(obter_desc_expressao(cd_exp_motivo,ds_motivo),1,80) 
into STRICT	ds_motivo_w 
from	qua_nc_motivo_subst 
where	nr_sequencia = nr_seq_motivo_subst_p;
 
ds_motivo_subst_w	:= substr(ds_motivo_subst_p,1,3700);
 
ds_historico_w	:=	substr(wheb_mensagem_pck.get_texto(305773) || chr(13) || chr(10) || 
				wheb_mensagem_pck.get_texto(305774) || ds_motivo_w || chr(13) || chr(10) || 
				ds_motivo_subst_w,1,3990);
 
insert into qua_nao_conform_hist( 
	nr_sequencia, 
	nr_seq_nao_conform, 
	dt_atualizacao, 
	nm_usuario, 
	ds_historico, 
	cd_pessoa_fisica, 
	dt_atualizacao_nrec, 
	nm_usuario_nrec, 
	dt_historico, 
	ie_origem) 
values (	nextval('qua_nao_conform_hist_seq'), 
	nr_sequencia_p, 
	clock_timestamp(), 
	nm_usuario_p, 
	ds_historico_w, 
	substr(Obter_Pessoa_Fisica_Usuario(nm_usuario_p, 'C'),1,10), 
	clock_timestamp(), 
	nm_usuario_p, 
	clock_timestamp(), 
	'S');
 
insert into qua_nao_conform_leitura( 
	nr_sequencia, 
	dt_atualizacao, 
	nm_usuario, 
	dt_atualizacao_nrec, 
	nm_usuario_nrec, 
	nr_seq_nao_conform, 
	nm_usuario_acesso, 
	dt_acesso) 
values (	nextval('qua_nao_conform_leitura_seq'), 
	clock_timestamp(), 
	nm_usuario_p, 
	clock_timestamp(), 
	nm_usuario_p, 
	nr_sequencia_p, 
	nm_usuario_p, 
	clock_timestamp());
 
commit;
 
end;
$body$
