CREATE OR REPLACE PROCEDURE qua_valid_doc_idioma ( nm_usuario_p text, nr_seq_docto_p bigint, cd_pessoa_validacao_p text, dt_validacao_p timestamp) AS $body$
BEGIN

if((nr_seq_docto_p IS NOT NULL AND nr_seq_docto_p::text <> '') and (cd_pessoa_validacao_p IS NOT NULL AND cd_pessoa_validacao_p::text <> '') and (dt_validacao_p IS NOT NULL AND dt_validacao_p::text <> ''))	then
