CREATE OR REPLACE PROCEDURE qua_vincular_acao_inef_causa ( nr_seq_causa_p bigint, nr_seq_acao_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


qt_acao_causa_w		bigint;


BEGIN

if (nr_seq_causa_p > 0) and (nr_seq_acao_p > 0) then

	select	count(*)
	into STRICT	qt_acao_causa_w
	from	qua_nc_causa_ac_inef
	where	nr_seq_causa	= nr_seq_causa_p
	and	nr_seq_acao	= nr_seq_acao_p;

	if (qt_acao_causa_w = 0) then
		insert into qua_nc_causa_ac_inef(
				nr_sequencia,
				nr_seq_causa,
				nr_seq_acao,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec)
			values (
				nextval('qua_nc_causa_ac_inef_seq'),
				nr_seq_causa_p,
				nr_seq_acao_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p);

		commit;
	end if;
end if;

end;
$body$
