CREATE OR REPLACE PROCEDURE qua_vincula_acao_rnc ( nr_seq_nao_conform_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_classif_nao_conform_w	bigint;


BEGIN

select	max(nr_seq_classificacao)
into STRICT	nr_seq_classif_nao_conform_w
from	qua_nao_conformidade
where	nr_sequencia = nr_seq_nao_conform_p;

update	man_ordem_servico
set	nr_seq_nao_conform = nr_seq_nao_conform_p,
	nr_seq_classif_nao_conform = nr_seq_classif_nao_conform_w,
	dt_atualizacao = clock_timestamp(),
	nm_usuario = nm_usuario_p
where	nr_sequencia = nr_sequencia_p;

commit;

end;
