SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE quebra_linha_hl7 ( nr_seq_laudo_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_conversao_w	bigint;
ie_posicao_separador_w	bigint;
qt_registro_w		bigint;
ds_texto_w		varchar(4000);
lista_texto_w		dbms_sql.varchar2_table;
qt_texto_laudo_w	bigint;


BEGIN

 nr_seq_conversao_w  := converte_rtf_string('	SELECT ds_laudo
			FROM laudo_paciente
			WHERE nr_sequencia = :nr_seq', nr_seq_laudo_p, nm_usuario_p, nr_seq_conversao_w );

begin
	select	ds_texto
	into STRICT	ds_texto_w
	from	tasy_conversao_rtf
	where	nr_sequencia = nr_seq_conversao_w;
exception
when others then
	ds_texto_w := null;
end;

if (ds_texto_w IS NOT NULL AND ds_texto_w::text <> '') then
ie_posicao_separador_w 	:= position(chr(10) in ds_texto_w);
qt_registro_w		:= 1;

	while(ds_texto_w IS NOT NULL AND ds_texto_w::text <> '') loop
		if (ie_posicao_separador_w = 0) then
			lista_texto_w(qt_registro_w) := trim(both substr(ds_texto_w, 1, length(ds_texto_w)));
			ds_texto_w := null;
		else
			lista_texto_w(qt_registro_w) := trim(both substr(ds_texto_w, 1, ie_posicao_separador_w - 1));
			ds_texto_w := substr(ds_texto_w, ie_posicao_separador_w + 1, length(ds_texto_w));
		end if;

		qt_registro_w := qt_registro_w + 1;

		-- atualiza condição do while
		ie_posicao_separador_w := position(chr(10) in ds_texto_w);
	end loop;


	if (lista_texto_w IS NOT NULL AND lista_texto_w::text <> '') then

		select	count(*)
		into STRICT	qt_texto_laudo_w
		from	w_texto_laudo_hl7 a
		where	a.nr_seq_laudo = nr_seq_laudo_p;

		if (qt_texto_laudo_w > 0) then
			delete	FROM w_texto_laudo_hl7
			where 	nr_seq_laudo = nr_seq_laudo_p;
		end if;

		for i in lista_texto_w.first..lista_texto_w.last loop
			begin
			if ((lista_texto_w(i) IS NOT NULL AND (lista_texto_w(i))::text <> '')) then

				insert 	into	w_texto_laudo_hl7(	NM_USUARIO,
									DT_ATUALIZACAO,
									DS_LAUDO,
									NR_SEQ_LAUDO,
									IE_ORDEM )
						values (	nm_usuario_p,
									clock_timestamp(),
									lista_texto_w(i),
									nr_seq_laudo_p,
									i);
			end if;
			end;
		end loop;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
