SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ratear_classif_titulo_neg_cr ( nr_seq_negociacao_p bigint, nr_titulo_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_conta_contabil_w		varchar(20);
vl_classificacao_w		double precision;
vl_classif_prop_w		double precision;
vl_tot_classif_w		double precision;
vl_negociacao_w			double precision;
vl_titulo_w			double precision;
vl_titulo_neg_w			double precision;
cd_conta_financ_w		bigint;
nr_titulo_neg_w			bigint;
cd_centro_custo_w		integer;
nr_seq_classif_w		integer;
ie_tipo_conta_financ_w	varchar(1);
cd_estabelecimento_w	smallint;
cd_cgc_w		varchar(14);
cd_pessoa_fisica_w	varchar(255);
nr_seq_conta_financ_w	bigint;
nr_seq_proj_rec_w		bigint;
ie_origem_titulo_w	varchar(10);
nr_seq_classe_tit_rec_w	bigint;
nr_seq_trans_fin_w	bigint;
ie_tipo_titulo_rec_w	titulo_receber.ie_tipo_titulo%type;
cd_moeda_w		titulo_receber.cd_moeda%type;
vl_negociado_w		double precision;	

C01 CURSOR FOR /* Classificacao do titulo */
	SELECT	b.cd_conta_financ,
		b.cd_conta_contabil,
		b.cd_centro_custo,
		b.vl_classificacao
	from	titulo_receber_classif	b,
		titulo_rec_negociado	a
	where	a.nr_titulo		= b.nr_titulo
	and	a.nr_seq_negociacao	= nr_seq_negociacao_p;/*
	group by
		b.cd_conta_financ,
		b.cd_conta_contabil,
		b.cd_centro_custo,
		b.nr_titulo;			*/
		


BEGIN

if (nr_seq_negociacao_p IS NOT NULL AND nr_seq_negociacao_p::text <> '') and (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	
	select	a.cd_estabelecimento,
		a.cd_cgc,
		a.nr_seq_proj_rec,
		a.vl_titulo,
		a.cd_pessoa_fisica,
		a.ie_origem_titulo,
		a.nr_seq_classe,
		a.nr_seq_trans_fin_contab,
		a.ie_tipo_titulo,
		a.cd_moeda
	into STRICT	cd_estabelecimento_w,
		cd_cgc_w,
		nr_seq_proj_rec_w,
		vl_titulo_w,
		cd_pessoa_fisica_w,
		ie_origem_titulo_w,
		nr_seq_classe_tit_rec_w,
		nr_seq_trans_fin_w,
		ie_tipo_titulo_rec_w,
		cd_moeda_w
	from	titulo_receber a
	where	a.nr_titulo		= nr_titulo_p;	

	select 	obter_valores_negociacao_cr(a.nr_sequencia,'VT')		
	into STRICT	vl_negociado_w
	from	negociacao_cr a
	where	a.nr_sequencia = nr_seq_negociacao_p;

	ie_tipo_conta_financ_w := obter_param_usuario(5514, 26, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_tipo_conta_financ_w);
	
	
	open C01; /* Inserir classificação proporcional */
	loop
	fetch C01 into	
		cd_conta_financ_w,
		cd_conta_contabil_w,
		cd_centro_custo_w,
		vl_classificacao_w/*,
		nr_titulo_neg_w*/
;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		/*select	a.vl_titulo
		into	vl_titulo_neg_w
		from	titulo_receber	a
		where	a.nr_titulo	= nr_titulo_neg_w;*/

		
		/*select	nvl(max(nr_sequencia),0) + 1
		into	nr_seq_classif_w
		from	titulo_receber_classif
		where	nr_titulo	= nr_titulo_p;*/
		
		if (coalesce(ie_tipo_conta_financ_w, 'T') = 'C') then
		
			nr_seq_conta_financ_w := obter_conta_financeira(	'E', cd_estabelecimento_w, null, null, null, null, null, cd_cgc_w, null, nr_seq_conta_financ_w, null, null, null, null, null, nr_seq_proj_rec_w, null, cd_pessoa_fisica_w, ie_origem_titulo_w, null, nr_seq_classe_tit_rec_w, null, null, nr_seq_trans_fin_w, null, null, null, ie_tipo_titulo_rec_w, cd_moeda_w);	

			if (coalesce(nr_seq_conta_financ_w,0) > 0) then
				cd_conta_financ_w := nr_seq_conta_financ_w;
			end if;
			
		end if;
		
		select	max(a.nr_sequencia)
		into STRICT	nr_seq_classif_w
		from	titulo_receber_classif a
		where	a.nr_titulo	= nr_titulo_p
		and	coalesce(a.cd_conta_financ,coalesce(cd_conta_financ_w,0))		= coalesce(cd_conta_financ_w,0)
		and	coalesce(a.cd_conta_contabil,coalesce(cd_conta_contabil_w,0))	= coalesce(cd_conta_contabil_w,0);

		if (coalesce(nr_seq_classif_w::text, '') = '') then
		
			select	coalesce(max(a.nr_sequencia),0) + 1
			into STRICT	nr_seq_classif_w
			from	titulo_receber_classif a
			where	a.nr_titulo	= nr_titulo_p;
		/*                                                            
		if	(vl_titulo_w <= vl_titulo_neg_w) then
			vl_classif_prop_w	:= dividir_sem_round(vl_titulo_w,vl_titulo_neg_w) * vl_classificacao_w;
		else
			vl_classif_prop_w	:= dividir_sem_round(vl_titulo_neg_w,vl_titulo_w) * vl_titulo_neg_w;
		end if;*/
		
			vl_classif_prop_w := dividir_sem_round(vl_titulo_w,vl_negociado_w) * vl_classificacao_w;
				
			insert into titulo_receber_classif(nr_titulo,
				nr_sequencia,
				nm_usuario,
				dt_atualizacao,
				cd_conta_financ,
				cd_conta_contabil,
				cd_centro_custo,
				vl_classificacao)
			values (nr_titulo_p,
				nr_seq_classif_w,
				nm_usuario_p,
				clock_timestamp(),
				cd_conta_financ_w,
				cd_conta_contabil_w,
				cd_centro_custo_w,
				vl_classif_prop_w);
		else
	
			update	titulo_receber_classif
			set		vl_classificacao	= vl_classificacao + dividir_sem_round(vl_titulo_w,vl_negociado_w) * vl_classificacao_w,
					vl_original			= vl_original + dividir_sem_round(vl_titulo_w,vl_negociado_w) * vl_classificacao_w
			where	nr_titulo			= nr_titulo_p
			and		nr_sequencia		= nr_seq_classif_w;
		end if;	
				
		end;
	end loop;
	close C01;

	select	sum(a.vl_classificacao) /* Arredondamento classificação*/
	into STRICT	vl_tot_classif_w
	from	titulo_receber_classif a
	where	a.nr_titulo	= nr_titulo_p;
	
	if (vl_tot_classif_w <> vl_titulo_w) then
		if (vl_tot_classif_w > vl_titulo_w) then
			update	titulo_receber_classif
			set	vl_classificacao	= vl_classificacao - abs(vl_titulo_w - vl_tot_classif_w)
			where	nr_titulo		= nr_titulo_p
			and	nr_sequencia		= nr_seq_classif_w;
		else
			update	titulo_receber_classif
			set	vl_classificacao	= vl_classificacao + (vl_titulo_w - vl_tot_classif_w)
			where	nr_titulo		= nr_titulo_p
			and	nr_sequencia		= nr_seq_classif_w;
		end if;
	end if;
end if;

/* Não pode ter commit */

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ratear_classif_titulo_neg_cr ( nr_seq_negociacao_p bigint, nr_titulo_p bigint, nm_usuario_p text) FROM PUBLIC;

