CREATE OR REPLACE PROCEDURE ratear_deducao_conv_conta ( nr_interno_conta_p bigint, nr_seq_conta_partic_p bigint, vl_base_com_imposto_p bigint, vl_base_sem_imposto_p bigint, vl_medico_p bigint, ie_coseguro_maximo_p text, pr_imposto_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_conta_w		conta_paciente.vl_conta%type := 0;


BEGIN

if (coalesce(nr_interno_conta_p,0) > 0) then

	vl_conta_w	:= coalesce(obter_valor_conta(nr_interno_conta_p,0),0);

	if (vl_conta_w > 0) then

		/*Dedução dos procedimentos*/

		CALL ratear_deducao_proc_conv_conta(nr_interno_conta_p,
									nr_seq_conta_partic_p,
									vl_conta_w,
									vl_base_com_imposto_p,
									vl_base_sem_imposto_p,
									vl_medico_p,
									ie_coseguro_maximo_p,
									nm_usuario_p);

		/*Dedução dos materiais*/

		CALL ratear_deducao_mat_conv_conta(nr_interno_conta_p,
									nr_seq_conta_partic_p,
									vl_conta_w,
									vl_base_com_imposto_p,
									vl_base_sem_imposto_p,
									vl_medico_p,
									ie_coseguro_maximo_p,
									nm_usuario_p);

		/*Rotina para ajustar o arredondamento do valor calculado com o valor das deduções*/

		CALL arredondar_deducao_conv_conta(nr_interno_conta_p, vl_base_com_imposto_p, vl_base_sem_imposto_p, ie_coseguro_maximo_p, pr_imposto_p, nm_usuario_p);

		/*Rotina para ajustar o arredondamento do valor da conta (o valor da conta + as deduções não pode ser diferente do valor inicial da conta)*/

		CALL arredondar_valores_conv_conta(nr_interno_conta_p, vl_conta_w, vl_base_com_imposto_p, nm_usuario_p);

	end if;

end if;

end;
$body$
