SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ratear_deducao_mat_conv_conta ( nr_interno_conta_p bigint, nr_seq_conta_partic_p bigint, vl_conta_original_p bigint, vl_base_com_imposto_p bigint, vl_base_sem_imposto_p bigint, vl_medico_p bigint, ie_coseguro_maximo_p text, nm_usuario_p text) AS $body$
DECLARE


cd_convenio_partic_w	conta_paciente.cd_convenio_parametro%type;
cd_categoria_partic_w	conta_paciente.cd_categoria_parametro%type;

nr_seq_mat_deduc_w	material_atend_paciente.nr_sequencia%type;

ie_tipo_calculo_w	conta_pac_deducao_conv.ie_tipo_calculo%type;
nr_seq_deducao_conv_w	conta_pac_deducao_conv.nr_sequencia%type;
vl_calculado_w		conta_pac_deducao_conv.vl_calculado%type;
vl_informado_calculo_w	conta_pac_deducao_conv.vl_calculado%type;
vl_calculado_com_imp_w		conta_pac_deducao_conv.vl_calculado_com_imp%type;
vl_calculado_sem_imp_w		conta_pac_deducao_conv.vl_calculado_sem_imp%type;

vl_itens_sem_tributo_w	double precision;

nr_seq_conta_ded_item_w	conta_pac_ded_conv_item.nr_sequencia%type;

nr_seq_mat_valor_w	mat_atend_paciente_valor.nr_sequencia%type;
ie_tipo_valor_w		mat_atend_paciente_valor.ie_tipo_valor%type;

vl_rateio_w		double precision := 0;
vl_rateio_com_imp_w		double precision := 0;
vl_rateio_sem_imp_w		double precision := 0;
vl_deducao_w		double precision := 0;
vl_diferenca_w		double precision := 0;

vl_max_coseguro_conta_w		conta_paciente.vl_maximo_coseguro%type := 0;
pr_sem_imposto_w	double precision;
pr_com_imposto_w	double precision;
vl_total_coseguro_w		conta_pac_deducao_conv.vl_calculado%type;

cursor_dados_mat CURSOR(nr_interno_conta_pc bigint) FOR
	SELECT	nr_sequencia,
			cd_material,
			qt_material,
			cd_unidade_medida,
			cd_acao,
			dt_entrada_unidade,
			cd_setor_atendimento,
			nr_seq_atepacu,
			nr_atendimento,
			dt_atendimento,
			dt_conta,
			vl_material,
			vl_unitario,
			ie_valor_informado,
			cd_convenio,
			cd_categoria,
			(SELECT max(x.pr_imposto) from matpaci_imposto x where x.nr_seq_matpaci = a.nr_sequencia) pr_imposto_item
	from	material_atend_paciente a
	where	nr_interno_conta = nr_interno_conta_pc
	and		coalesce(nr_seq_proc_pacote::text, '') = ''
	order by
			pr_imposto_item,
			vl_material;

c02 CURSOR FOR
	SELECT	ie_tipo_calculo,
			nr_sequencia,
			vl_calculado,
			vl_calculado_com_imp,
			vl_calculado_sem_imp,
			vl_informado_calculo
	from	conta_pac_deducao_conv
	where	nr_seq_conta_orig = nr_interno_conta_p
	and	coalesce(vl_calculado,0) <> 0;

BEGIN

if (coalesce(nr_interno_conta_p,0) > 0) then
	select	coalesce(max(vl_maximo_coseguro),0)
	into STRICT	vl_max_coseguro_conta_w
	from	conta_paciente
	where	nr_interno_conta = nr_interno_conta_p;

	select	max(cd_convenio_parametro),
			max(cd_categoria_parametro)
	into STRICT	cd_convenio_partic_w,
			cd_categoria_partic_w
	from	conta_paciente
	where	nr_interno_conta = nr_seq_conta_partic_p;

	select	coalesce(sum(a.vl_calculado),0)
	into STRICT	vl_total_coseguro_w
	from	conta_pac_deducao_conv a
	where	a.ie_tipo_calculo in ('H','N','A')
	and		a.nr_seq_conta_orig = nr_interno_conta_p;

	if (vl_conta_original_p > 0) then
		pr_sem_imposto_w	:= vl_base_sem_imposto_p / vl_conta_original_p;
		pr_com_imposto_w	:= vl_base_com_imposto_p / vl_conta_original_p;
	end if;

	for dados_mat in cursor_dados_mat(nr_interno_conta_p) loop

		vl_deducao_w		:= 0;
		nr_seq_mat_deduc_w	:= null;

		if (dados_mat.vl_material <> 0) then

			select	nextval('material_atend_paciente_seq')
			into STRICT	nr_seq_mat_deduc_w
			;

			insert into material_atend_paciente(nr_sequencia,
				cd_material,
				qt_material,
				ie_valor_informado,
				cd_unidade_medida,
				cd_acao,
				dt_entrada_unidade,
				cd_setor_atendimento,
				nr_seq_atepacu,
				nr_atendimento,
				dt_atendimento,
				dt_conta,
				dt_atualizacao,
				nm_usuario,
				cd_convenio,
				vl_material,
				vl_unitario,
				cd_categoria,
				ie_auditoria,
				nr_interno_conta)
			values (nr_seq_mat_deduc_w,					-- nr_sequencia
				dados_mat.cd_material,					-- cd_material
				0,/*dados_mat.qt_material,					-- qt_material*/
				'S',							-- ie_valor_informado
				dados_mat.cd_unidade_medida,				-- cd_unidade_medida
				dados_mat.cd_acao,					-- cd_acao
				dados_mat.dt_entrada_unidade,				-- dt_entrada_unidade
				dados_mat.cd_setor_atendimento,				-- cd_setor_atendimento
				dados_mat.nr_seq_atepacu,				-- nr_seq_atepacu
				dados_mat.nr_atendimento,				-- nr_atendimento
				dados_mat.dt_atendimento,				-- dt_atendimento
				dados_mat.dt_conta,
				clock_timestamp(),						-- dt_atualizacao
				nm_usuario_p,						-- nm_usuario
				cd_convenio_partic_w,					-- cd_convenio
				0,							-- vl_material
				0,							-- vl_unitario
				cd_categoria_partic_w,					-- cd_categoria
				'N',							-- ie_auditoria
				nr_seq_conta_partic_p					-- nr_interno_conta
				);

			if (dados_mat.ie_valor_informado = 'S') then
				ie_tipo_valor_w	:= 95;
			else
				ie_tipo_valor_w	:= 94;
			end if;

			delete	from mat_atend_paciente_valor
			where	nr_seq_material		= dados_mat.nr_sequencia
			and	ie_tipo_valor		in (94,95);

			select 	coalesce(max(nr_sequencia), 0) + 1
			into STRICT	nr_seq_mat_valor_w
			from	mat_atend_paciente_valor
			where	nr_seq_material		= dados_mat.nr_sequencia;

			insert into mat_atend_paciente_valor(nr_seq_material,
				nr_sequencia,
				ie_tipo_valor,
				dt_atualizacao,
				nm_usuario,
				vl_material,
				cd_convenio,
				cd_categoria,
				pr_valor)
			values (dados_mat.nr_sequencia,
				nr_seq_mat_valor_w,
				ie_tipo_valor_w,
				clock_timestamp(),
				nm_usuario_p,
				dados_mat.vl_material,
				dados_mat.cd_convenio,
				dados_mat.cd_categoria,
				0);

			open c02;
			loop
				fetch	c02
				into	ie_tipo_calculo_w,
						nr_seq_deducao_conv_w,
						vl_calculado_w,
						vl_calculado_com_imp_w,
						vl_calculado_sem_imp_w,
						vl_informado_calculo_w;
				EXIT WHEN NOT FOUND; /* apply on c02 */
				begin
				vl_rateio_com_imp_w	:= 0;
				vl_rateio_sem_imp_w	:= 0;
				if (ie_tipo_calculo_w = 'D') then
					if (dados_mat.pr_imposto_item > 0) then
						vl_rateio_com_imp_w	:= (dados_mat.vl_material * dividir_sem_round(vl_calculado_com_imp_w,vl_base_com_imposto_p));
						/* Retirar valor de imposto */

						--vl_rateio_com_imp_w	:= vl_rateio_w / ((dados_mat.pr_imposto_item + 100)/100);
					else
						vl_rateio_sem_imp_w	:= (dados_mat.vl_material * dividir_sem_round(vl_calculado_sem_imp_w,vl_base_sem_imposto_p));
					end if;
				elsif (ie_tipo_calculo_w in ('H','A','N')) and (ie_coseguro_maximo_p = 'S') then
					if (dados_mat.pr_imposto_item > 0) then
						vl_rateio_com_imp_w	:= dados_mat.vl_material * dividir_sem_round(vl_calculado_com_imp_w,vl_base_com_imposto_p);
						/* proporcional */

						--vl_rateio_com_imp_w	:= vl_rateio_w * dividir_sem_round(vl_calculado_w,vl_total_coseguro_w);
						/* Retirar valor de imposto */

						--vl_rateio_com_imp_w	:= vl_rateio_w / ((dados_mat.pr_imposto_item + 100)/100);
					else
						vl_rateio_sem_imp_w	:= dados_mat.vl_material * dividir_sem_round(vl_calculado_sem_imp_w,vl_base_sem_imposto_p);
						/* proporcional */

						--vl_rateio_sem_imp_w	:= vl_rateio_w * dividir_sem_round(vl_calculado_w,vl_total_coseguro_w);
					end if;
				else
					/* Para coaseguro imposto entra depois */

					if (dados_mat.pr_imposto_item > 0) then
						vl_rateio_com_imp_w	:= dados_mat.vl_material * dividir_sem_round(vl_calculado_com_imp_w,vl_base_com_imposto_p);
					else
						vl_rateio_sem_imp_w	:= dados_mat.vl_material * dividir_sem_round(vl_calculado_sem_imp_w,vl_base_sem_imposto_p);
					end if;
				end if;

				vl_rateio_w	:= coalesce(vl_rateio_com_imp_w,0) + coalesce(vl_rateio_sem_imp_w,0);

				vl_deducao_w	:= vl_deducao_w + vl_rateio_w;

				select	nextval('conta_pac_ded_conv_item_seq')
				into STRICT	nr_seq_conta_ded_item_w
				;

				insert into conta_pac_ded_conv_item(
					nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_deducao_conv,
					nr_seq_matpaci_origem,
					nr_seq_matpaci_dest,
					vl_rateio,
					vl_rateio_com_imp,
					vl_rateio_sem_imp,
					nr_seq_mat_atend_pac_valor
				) values (
					nr_seq_conta_ded_item_w,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_deducao_conv_w,
					dados_mat.nr_sequencia,
					nr_seq_mat_deduc_w,
					vl_rateio_w,
					vl_rateio_com_imp_w,
					vl_rateio_sem_imp_w,
					null
				);

				end;
			end loop;
			close c02;

			/*Deduzir o valor dos itens e jogar a diferença na conta particular*/

			update	material_atend_paciente
			set	vl_material = vl_material - vl_deducao_w,
				vl_unitario = dividir_sem_round(vl_material - vl_deducao_w, qt_material),
				ie_valor_informado = 'S'
			where	nr_sequencia = dados_mat.nr_sequencia;

			/*Atualizar o imposto do item após a dedução*/

			CALL gerar_tributo_conta_pac(nr_interno_conta_p, dados_mat.nr_sequencia, 'M', nm_usuario_p);

			update	material_atend_paciente
			set	vl_material = vl_deducao_w,
				vl_unitario = dividir_sem_round(vl_deducao_w, qt_material)
			where	nr_sequencia = nr_seq_mat_deduc_w;

			/*Atualizar o imposto do item após a dedução*/

			CALL gerar_tributo_conta_pac(nr_seq_conta_partic_p, nr_seq_mat_deduc_w, 'M', nm_usuario_p);

			/*Atualizar o imposto e informações do item gerado com a dedução*/

			CALL atualiza_preco_material(nr_seq_mat_deduc_w, nm_usuario_p);

		end if;

	end loop;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ratear_deducao_mat_conv_conta ( nr_interno_conta_p bigint, nr_seq_conta_partic_p bigint, vl_conta_original_p bigint, vl_base_com_imposto_p bigint, vl_base_sem_imposto_p bigint, vl_medico_p bigint, ie_coseguro_maximo_p text, nm_usuario_p text) FROM PUBLIC;

