SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ratear_item_ordem_compra_cc ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, nr_seq_criterio_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, dt_vigencia_p timestamp, cd_centro_custo_p bigint, qt_registros_p bigint, ie_seleciona_cc_p text, ie_primeira_vez_p text) AS $body$
DECLARE


nr_item_oci_w		bigint;
vl_total_item_ordem_w	double precision;
cd_centro_custo_w	integer;
cd_conta_contabil_w	varchar(20);
cd_conta_financ_w	bigint;
ds_erro_w		varchar(2000);
pr_rateio_w		double precision;
vl_rateio_w		double precision;
vl_total_rateio_w	double precision;
nr_sequencia_w		bigint;

c00 CURSOR FOR
SELECT	nr_item_oci,
	(vl_unitario_material * qt_material)
from	ordem_compra_item
where	nr_ordem_compra		= nr_ordem_compra_p
and (nr_item_oci = nr_item_oci_p or nr_item_oci_p = 0);


BEGIN

if (ie_primeira_vez_p = 'S') then

	delete from ordem_compra_item_rateio
	where	nr_ordem_compra	= nr_ordem_compra_p
	and	nr_item_oci	= nr_item_oci_p;

end if;

open c00;
loop
fetch c00 into
	nr_item_oci_w,
	vl_total_item_ordem_w;
EXIT WHEN NOT FOUND; /* apply on c00 */
	begin

	vl_total_rateio_w	:= 0;
	vl_rateio_w		:= 0;

	select	nextval('ordem_compra_item_rateio_seq')
	into STRICT	nr_sequencia_w
	;

	pr_rateio_w := dividir(100,coalesce(qt_registros_p,0));
	vl_rateio_w := coalesce(dividir((coalesce(vl_total_item_ordem_w,0) * pr_rateio_w), 100),0);

	if (nr_seq_criterio_p > 0) then
		select	cd_centro_custo,
			cd_conta_contabil,
			cd_conta_financ
		into STRICT	cd_centro_custo_w,
			cd_conta_contabil_w,
			cd_conta_financ_w
		from	ctb_criterio_rateio_item
		where	nr_seq_criterio = nr_seq_criterio_p
		and	cd_centro_custo = cd_centro_custo_p
		and (dt_vigencia_p >= coalesce(dt_inicio_vigencia,dt_vigencia_p) and dt_vigencia_p <= coalesce(dt_fim_vigencia,dt_vigencia_p));
	end if;

	begin
	insert	into ordem_compra_item_rateio(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_ordem_compra,
			nr_item_oci,
			cd_centro_custo,
			cd_conta_contabil,
			cd_conta_financ,
			nr_seq_criterio_rateio,
			vl_rateio,
			vl_frete,
			vl_desconto,
			vl_seguro,
			vl_despesa_acessoria,
			ie_situacao)
		values (	nr_sequencia_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_ordem_compra_p,
			nr_item_oci_w,
			coalesce(cd_centro_custo_w,cd_centro_custo_p),
			cd_conta_contabil_w,
			cd_conta_financ_w,
			CASE WHEN nr_seq_criterio_p=0 THEN null  ELSE nr_seq_criterio_p END ,
			vl_rateio_w,
			0,
			0,
			0,
			0,
			'A');
		exception when others then
			ds_erro_w	:= SQLERRM(SQLSTATE);
			CALL wheb_mensagem_pck.exibir_mensagem_abort(190145, 'NR_SEQUENCIA_W=' || nr_sequencia_w || ';' || 'DS_ERRO_W=' || ds_erro_w);
		end;

	update	ordem_compra_item
	set	nr_seq_criterio_rateio = CASE WHEN nr_seq_criterio_p=0 THEN null  ELSE nr_seq_criterio_p END
	where	nr_ordem_compra	= nr_ordem_compra_p
	and	nr_item_oci	= nr_item_oci_w;

	end;
end loop;
close c00;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ratear_item_ordem_compra_cc ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, nr_seq_criterio_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, dt_vigencia_p timestamp, cd_centro_custo_p bigint, qt_registros_p bigint, ie_seleciona_cc_p text, ie_primeira_vez_p text) FROM PUBLIC;

