SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ratear_tributo_tit_guia (nr_titulo_p bigint, vl_pago_guia_p bigint, vl_tributo_guia_p INOUT bigint) AS $body$
DECLARE


vl_tributo_titulo_w	titulo_receber_trib.vl_tributo%type;
vl_tributo_guia_w	titulo_receber_trib.vl_tributo%type;
vl_titulo_w		titulo_receber.vl_titulo%type;


BEGIN

vl_tributo_guia_w	:= 0;

select	max(vl_titulo)
into STRICT	vl_titulo_w
from	titulo_receber
where	nr_titulo	= nr_titulo_p;

/*Busca o total de tributos do tÃ­tulo que deduzem do saldo*/

select	coalesce(sum(vl_tributo),0)
into STRICT	vl_tributo_titulo_w
from	titulo_receber_trib
where	nr_titulo		= nr_titulo_p
and	coalesce(ie_origem_tributo, 'C') in ('D','CD');

if (vl_tributo_titulo_w > 0) and (vl_pago_guia_p > 0) then

	vl_tributo_guia_w	:= round(((vl_pago_guia_p / vl_titulo_w) * vl_tributo_titulo_w),2);

end if;

vl_tributo_guia_p	:= vl_tributo_guia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
