CREATE OR REPLACE PROCEDURE ratear_valor_glosado (ie_tipo_p bigint, nr_sequencia_p bigint, vl_total_p bigint, cd_motivo_glosa_p bigint, vl_informado_p bigint) AS $body$
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	if (ie_tipo_p = 0) then
		update    convenio_retorno_glosa
		set       vl_glosa 			= coalesce(dividir_sem_round((vl_informado_p * vl_cobrado), vl_total_p),0)
		where     nr_sequencia    		= nr_sequencia_p;

	elsif (ie_tipo_p = 1) then

		update    convenio_retorno_glosa
		set       vl_glosa 			= coalesce(dividir_sem_round(((vl_total_p - vl_informado_p) * vl_cobrado), vl_total_p),0)
		where     nr_sequencia    		= nr_sequencia_p;

	elsif (ie_tipo_p = 2) then

		update    convenio_retorno_glosa
		set       vl_glosa 			= 0,
			  vl_amaior 			= coalesce(dividir_sem_round((vl_informado_p * vl_cobrado), vl_total_p),0)
		where     nr_sequencia    		= nr_sequencia_p;

	elsif (ie_tipo_p = 3) then

		update    convenio_retorno_glosa
		set       vl_glosa 			= coalesce(dividir_sem_round((vl_informado_p * vl_cobrado), vl_total_p),0),
		          cd_motivo_glosa 		= cd_motivo_glosa_p
		where     nr_sequencia    		= nr_sequencia_p;

	elsif (ie_tipo_p = 4) then

		update    convenio_retorno_glosa
		set       vl_glosa 			= coalesce(dividir_sem_round(((vl_total_p - vl_informado_p) * vl_cobrado), vl_total_p),0),
		          cd_motivo_glosa 		= cd_motivo_glosa_p
		where     nr_sequencia    		= nr_sequencia_p;

	elsif (ie_tipo_p = 5) then

		update    convenio_retorno_glosa
		set       vl_glosa 			= 0,
			  vl_amaior 			= coalesce(dividir_sem_round((vl_informado_p * vl_cobrado), vl_total_p),0),
		          cd_motivo_glosa 		= cd_motivo_glosa_p
		where     nr_sequencia    		= nr_sequencia_p;
	end if;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
