SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE reabrir_caixa_saldo_diario (nr_seq_caixa_p bigint, nr_seq_saldo_p bigint, dt_saldo_p timestamp, nm_usuario_p text) AS $body$
DECLARE


ie_reabrir_saldo_contab_w			varchar(1);
ie_saldo_aberto_w					varchar(1);
ie_evento_caixa_w					varchar(1);
qt_dias_reabrir_saldo_w				bigint;
nr_lote_contabil_w					bigint;


BEGIN

ie_reabrir_saldo_contab_w := coalesce(obter_valor_param_usuario(813,72, obter_perfil_ativo, nm_usuario_p, 0),'N');
qt_dias_reabrir_saldo_w	:= coalesce(obter_valor_param_usuario(813,189, obter_perfil_ativo, nm_usuario_p, 0),0);

if (ie_reabrir_saldo_contab_w = 'N') then
	select 	coalesce(max(nr_lote_contabil),0)
	into STRICT	nr_lote_contabil_w
	from	caixa_saldo_diario
	where 	nr_sequencia = nr_seq_saldo_p;

	if (nr_lote_contabil_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(121771);
	end if;
end if;

if (qt_dias_reabrir_saldo_w > 0) and (trunc(clock_timestamp() - qt_dias_reabrir_saldo_w,'dd') > trunc(dt_saldo_p,'dd')) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(289671,'QT_DIAS=' || qt_dias_reabrir_saldo_w);
end if;

select	obter_se_saldo_caixa_aberto(null,nr_seq_saldo_p,dt_saldo_p,nm_usuario_p)
into STRICT	ie_saldo_aberto_w
;

if (ie_saldo_aberto_w = 'S') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(121766);
end if;

-- Valida se existem saldos pendentes de fechamento menor que a data_saldo
select 	obter_se_evento_caixa(nr_seq_caixa_p,'A',dt_saldo_p)
into STRICT	ie_evento_caixa_w
;

if (coalesce(ie_evento_caixa_w,'S') = 'N') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(121619);
end if;

update	caixa_saldo_diario
set 	dt_fechamento  = NULL,
	nm_usuario_fechamento  = NULL,
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where	nr_sequencia = nr_seq_saldo_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
