CREATE OR REPLACE PROCEDURE reabrir_lote_fluxo_caixa ( nr_seq_lote_fluxo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ie_classif_fluxo_w	fluxo_caixa_lote.ie_classif_fluxo%type;
dt_referencia_w		timestamp;
qt_reg_w		bigint	:= 0;
ds_historico_w		fluxo_caixa_lote_hist.ds_historico%type;


BEGIN

select	a.ie_classif_fluxo,
	a.dt_mesano_referencia
into STRICT	ie_classif_fluxo_w,
	dt_referencia_w
from	fluxo_caixa_lote a
where	a.nr_sequencia	= nr_seq_lote_fluxo_p
and	(a.dt_geracao IS NOT NULL AND a.dt_geracao::text <> '')
and	(a.dt_fechamento IS NOT NULL AND a.dt_fechamento::text <> '');

select	count(*)
into STRICT	qt_reg_w
from	fluxo_caixa_lote a
where	(a.dt_geracao IS NOT NULL AND a.dt_geracao::text <> '')
and	(a.dt_fechamento IS NOT NULL AND a.dt_fechamento::text <> '')
and	a.ie_classif_fluxo	= ie_classif_fluxo_w
and	PKG_DATE_UTILS.start_of(a.dt_mesano_referencia,'month',0) = PKG_DATE_UTILS.start_of(PKG_DATE_UTILS.ADD_MONTH(dt_referencia_w,1,0),'month',0)
and	coalesce(a.cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p;

if (qt_reg_w = 0) then
	update	fluxo_caixa_lote
	set	dt_fechamento	 = NULL,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_lote_fluxo_p;
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(331977);
end if;

ds_historico_w	:= substr(wheb_mensagem_pck.get_texto(290756),1,4000); -- Reabertura do fluxo de caixa lote
CALL GERAR_HISTORICO_FLUXO_LOTE(	nr_seq_lote_fluxo_p,
				'S',
				ds_historico_w,
				nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
