CREATE OR REPLACE PROCEDURE reabrir_saldo_diario_banco ( nr_seq_banco_saldo_p bigint, dt_referencia_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_inicial_w	timestamp;
dt_final_w	timestamp;
nr_seq_conta_banco_w	banco_saldo.nr_seq_conta%type;


BEGIN

dt_inicial_w	:= trunc(dt_referencia_p);
dt_final_w	:= fim_dia(dt_referencia_p);

select	nr_seq_conta
into STRICT	nr_seq_conta_banco_w
from	banco_saldo
where	nr_sequencia = nr_seq_banco_saldo_p;

delete	FROM fechamento_banco
where	dt_fechamento		between dt_inicial_w and dt_final_w
and	nr_seq_conta_banco	= nr_seq_conta_banco_w
and	cd_estabelecimento	= cd_estabelecimento_p;

CALL gerar_lancamento_banco(nr_seq_banco_saldo_p,nm_usuario_p,'N','DE',dt_referencia_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
