CREATE OR REPLACE PROCEDURE reagendar_item_oc_fluxo ( nr_ordem_compra_p bigint, ie_origem_p text, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE



c01 CURSOR FOR
	SELECT	a.nr_ordem_compra,
		a.NR_SEQUENCIA,
		a.DT_PREVISTA_ENTREGA,
		a.nm_usuario
	from	ORDEM_COMPRA_ITEM_ENTREGA a
	where	a.nr_ordem_compra = nr_ordem_compra_p;

	vet01		c01%rowtype;


BEGIN

		open c01;
		loop
		fetch c01 into
			vet01;
		EXIT WHEN NOT FOUND; /* apply on c01 */

				/* Inicia o processamento dos documentos conforme sua origem */

				if (ie_origem_p  ('OC') and ie_opcao_p = 'A') then

					CALL gravar_agend_fluxo_caixa(vet01.nr_ordem_compra,vet01.NR_SEQUENCIA,'OC', vet01.DT_PREVISTA_ENTREGA,'A',vet01.nm_usuario,'S');

				end if;


				if (ie_origem_p in ('OC') and ie_opcao_p = 'B') then

					CALL gravar_agend_fluxo_caixa(vet01.nr_ordem_compra,vet01.NR_SEQUENCIA,'OC', vet01.DT_PREVISTA_ENTREGA,'E',vet01.nm_usuario,'S');

				end if;

		end loop;
		close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
