SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE realizar_coleta_adep ( nr_controle_p text, nr_seq_etiqueta_p text, nm_usuario_p text) AS $body$
DECLARE


nr_prescricao_w	bigint;
nr_seq_prescr_w	bigint;


BEGIN
if (nr_controle_p IS NOT NULL AND nr_controle_p::text <> '') and (nr_seq_etiqueta_p IS NOT NULL AND nr_seq_etiqueta_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	select 	max(nr_prescricao)
	into STRICT	nr_prescricao_w
	from 	prescr_medica
	where 	nr_controle   = nr_controle_p;


	select 	max(nr_seq_prescr)
	into STRICT	nr_seq_prescr_w
	from 	prescr_proc_controle_etiq
	where 	nr_controle 	= nr_controle_p
	and 	nr_seq_etiqueta = nr_seq_etiqueta_p;

	if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') and (nr_seq_prescr_w IS NOT NULL AND nr_seq_prescr_w::text <> '') then
		begin

		update	prescr_proc_hor
		set	dt_fim_horario	= clock_timestamp(),
			nm_usuario_adm	= nm_usuario_p
		where	nr_seq_procedimento = nr_seq_prescr_w
		and	nr_prescricao = nr_prescricao_w
		and	coalesce(dt_fim_horario::text, '') = ''
		and	coalesce(dt_suspensao::text, '') = '';

		update	prescr_procedimento
		set	dt_coleta = clock_timestamp(),
			ie_amostra = 'S',
			nm_usuario = nm_usuario_p
		where	nr_prescricao = nr_prescricao_w
		and	nr_sequencia = nr_seq_prescr_w;

		update	prescr_procedimento
		set	ie_Status_atend	= 20
		where	nr_prescricao = nr_prescricao_w
		and	nr_sequencia = nr_seq_prescr_w
		and	ie_Status_atend < 20
		and	(nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '');

		end;
	end if;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
