CREATE OR REPLACE PROCEDURE realizar_todos_proc_previstos (nr_cirurgia_p bigint, nr_cirurgia_dbp_p bigint, nr_prescricao_p bigint, cd_local_estoque_p bigint default null, nm_usuario_p text DEFAULT NULL, cd_estabelecimento_p bigint DEFAULT NULL) AS $body$
DECLARE


ie_atualiza_proc_partic_cir_w	varchar(1) := 'N';


BEGIN

if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (nr_cirurgia_dbp_p IS NOT NULL AND nr_cirurgia_dbp_p::text <> '') then
	begin
	
