	CALL executar_taxa_equipamento(nr_cirurgia_p, nm_usuario_p);
	
	CALL gerar_lancto_auto_dispositivos(nr_cirurgia_p, cd_estabelecimento_p, nm_usuario_p);
	
	CALL executar_material_equipamento(nr_cirurgia_p, cd_local_estoque_p, nm_usuario_p);

	ie_atualiza_proc_partic_cir_w := obter_param_usuario(872, 329, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_atualiza_proc_partic_cir_w);
	
	if (ie_atualiza_proc_partic_cir_w = 'S') then
		begin
	
		CALL atualizar_proc_partic_cirurgia(	nr_cirurgia_p,cd_estabelecimento_p,nm_usuario_p);
	
		end;
	end if;
	
	end;
end if;

