CREATE OR REPLACE PROCEDURE realiza_proc_prev_grafico_pepo ( nr_seq_pepo_p bigint, nr_cirurgia_p bigint, nr_prescricao_p bigint, nm_usuario_p text ) AS $body$
DECLARE


nr_cirurgia_w	bigint;
nr_prescricao_w	bigint;

c01 CURSOR FOR
	SELECT	nr_cirurgia,
		nr_prescricao 
	from	cirurgia
	where	nr_seq_pepo = nr_seq_pepo_p;


BEGIN

if (coalesce(nr_seq_pepo_p,0) > 0) then
	begin
	open c01;
	loop
	fetch c01 into 	nr_cirurgia_w,
			nr_prescricao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		CALL realizar_proc_previstos(nr_cirurgia_w, nr_prescricao_w, nm_usuario_p, 'S');
		CALL executar_taxa_equipamento(nr_cirurgia_w, nm_usuario_p);
		end;
	end loop;
	close c01;
	end;
else
	begin
	CALL realizar_proc_previstos(nr_cirurgia_p, nr_prescricao_p, nm_usuario_p, 'S');
	CALL executar_taxa_equipamento(nr_cirurgia_p, nm_usuario_p);
	end;
end if;
