CREATE OR REPLACE PROCEDURE reaprazar_processo_horario ( nr_atendimento_p bigint, nr_seq_horario_p bigint, nr_seq_processo_p bigint, dt_horario_processo_p timestamp, dt_horario_reaprazar_p timestamp, nm_usuario_p text) AS $body$
DECLARE

				
ie_outros_horarios_w	varchar(1);
nr_seq_processo_w	bigint;
				

BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') and (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') and (dt_horario_processo_p IS NOT NULL AND dt_horario_processo_p::text <> '') and (dt_horario_reaprazar_p IS NOT NULL AND dt_horario_reaprazar_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	
	select	coalesce(max('S'),'N')
	into STRICT	ie_outros_horarios_w
	from	prescr_mat_hor
	where	nr_seq_processo	= nr_seq_processo_p
	and	nr_sequencia	<> nr_seq_horario_p
	and	coalesce(nr_seq_superior::text, '') = ''
	and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S'
	and	ie_agrupador not in (4,5);
	
	if (ie_outros_horarios_w = 'N') then
		begin
		update	adep_processo
		set	dt_horario_processo	= dt_horario_reaprazar_p,
			dt_atualizacao = clock_timestamp()
		where	nr_sequencia		= nr_seq_processo_p;
		end;
	else
		begin
		nr_seq_processo_w := adep_desdobrar_processo(nr_seq_processo_p, nr_seq_horario_p, null, nm_usuario_p, nr_seq_processo_w);
		
		update	adep_processo
		set	dt_horario_processo	= dt_horario_reaprazar_p,
			dt_atualizacao = clock_timestamp()
		where	nr_sequencia		= nr_seq_processo_w;
		end;
	end if;	

	end;
end if;
if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
