CREATE OR REPLACE PROCEDURE reatualiza_codigo_convenio (nr_seq_protocolo_p bigint, dt_ref_inicio_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
nr_interno_conta_w		bigint 		:= 0;

c01 CURSOR FOR 
SELECT 	nr_interno_conta 
from 	Conta_Paciente 
where 	nr_seq_protocolo 		= nr_seq_protocolo_p 
 and  nr_seq_protocolo_p	<> 0 

union
 
SELECT 	a.nr_interno_conta 
from 	Conta_Paciente a, protocolo_convenio b 
where 	a.nr_seq_protocolo 	= b.nr_seq_protocolo 
 and 	trunc(b.dt_mesano_referencia,'month') >= dt_ref_inicio_p 
 and  nr_seq_protocolo_p	= 0;


BEGIN 
 
open C01;
loop 
	fetch C01 	into 
   	nr_interno_conta_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		CALL Atualiza_Codigo_Convenio(nr_interno_conta_w,null);
end loop;
close C01;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
