CREATE OR REPLACE PROCEDURE recalcular_clas_bx_lote_cont ( nr_lote_contabil_p bigint, nm_usuario_p text) AS $body$
DECLARE

						 
nr_titulo_w	bigint;
nr_seq_baixa_w	bigint;

c01 CURSOR FOR 
SELECT	a.nr_titulo, 
	a.nr_sequencia 
from	titulo_receber_liq a 
where	a.nr_lote_contabil	= nr_lote_contabil_p 

union all
 
SELECT	a.nr_titulo, 
	a.nr_sequencia 
from	titulo_receber_liq a 
where	a.nr_lote_contab_antecip	= nr_lote_contabil_p 

union all
 
select	a.nr_titulo, 
	a.nr_sequencia 
from	titulo_receber_liq a 
where	a.nr_lote_contab_pro_rata	= nr_lote_contabil_p;


BEGIN 
 
open c01;
loop 
fetch c01 into	 
	nr_titulo_w, 
	nr_seq_baixa_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	CALL RECALCULAR_TITULO_REC_LIQ_CC(nr_titulo_w,nr_seq_baixa_w,nm_usuario_p);
	end;
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
