CREATE OR REPLACE PROCEDURE recalcular_contas_atend ( nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_interno_conta_w	bigint;

C01 CURSOR FOR
	SELECT	nr_interno_conta
	from	conta_paciente
	where	ie_status_acerto = 1
	and 	nr_atendimento = nr_atendimento_p
	order by nr_interno_conta;


BEGIN

open C01;
loop
fetch C01 into
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	CALL recalcular_conta_paciente(nr_interno_conta_w, nm_usuario_p);

	end;
end loop;
close C01;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
