CREATE OR REPLACE PROCEDURE recalcular_conta_terceiro (nr_seq_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w				bigint := 0;

c01 CURSOR FOR 
SELECT nr_sequencia 
from terceiro_operacao 
where nr_seq_conta = nr_seq_conta_p;


BEGIN 
open c01;
loop 
  	fetch c01 into 	nr_sequencia_w;
  	EXIT WHEN NOT FOUND; /* apply on c01 */
	CALL atualizar_operacao_terceiro(nr_sequencia_w, nm_usuario_p);
end loop;
	 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
