SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE recalcular_dose_prescr ( ie_tipo_perc_p bigint, qt_percentual_p bigint, nr_seq_atendimento_p bigint, nr_seq_material_p bigint) AS $body$
DECLARE


/*	ie_tipo_perc_p
	1 - Aumento
	2 - Redução	*/
BEGIN

if (qt_percentual_p IS NOT NULL AND qt_percentual_p::text <> '') then
	begin
	if (ie_tipo_perc_p = 1) then
		begin
		update	paciente_atend_medic
		set	qt_dose_prescricao	= (qt_dose_prescricao + ((qt_percentual_p * coalesce(qt_dose_prescricao,0)) / 100))
		where	nr_seq_atendimento	= nr_seq_atendimento_p
		and	nr_seq_material	= nr_seq_material_p;
		end;

	elsif (ie_tipo_perc_p = 2) then
		begin
		update	paciente_atend_medic
		set	qt_dose_prescricao	= (qt_dose_prescricao - ((qt_percentual_p * coalesce(qt_dose_prescricao,0)) / 100))
		where	nr_seq_atendimento	= nr_seq_atendimento_p
		and	nr_seq_material	= nr_seq_material_p;
		end;
	end if;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
