CREATE OR REPLACE PROCEDURE recalcular_retorno_repasse (nr_seq_retorno_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_procedimento_w	bigint;
nr_seq_material_w	bigint;

c01 CURSOR FOR 
SELECT	a.nr_seq_procedimento, 
	0 
from	procedimento_repasse a, 
	convenio_retorno_glosa b, 
	convenio_retorno_item c 
where	a.nr_seq_procedimento		= b.nr_seq_propaci 
and	b.nr_seq_ret_item		= c.nr_sequencia 
and	c.nr_seq_retorno		= nr_seq_retorno_p 
and	coalesce(a.nr_seq_procedimento, 0)	> 0 

union
 
SELECT	a.nr_sequencia, 
	0 
from	procedimento_paciente a, 
	convenio_retorno_item c 
where	a.nr_interno_conta		= c.nr_interno_conta 
and	c.nr_seq_retorno		= nr_seq_retorno_p 

union
 
select	0, 
	a.nr_seq_material 
from	material_repasse a, 
	convenio_retorno_glosa b, 
	convenio_retorno_item c 
where	a.nr_seq_material		= b.nr_seq_matpaci 
and	b.nr_seq_ret_item		= c.nr_sequencia 
and	c.nr_seq_retorno		= nr_seq_retorno_p 
and	coalesce(a.nr_seq_material, 0)	> 0 

union
	 
select	0, 
	a.nr_sequencia 
from	material_atend_paciente a, 
	convenio_retorno_item c 
where	a.nr_interno_conta		= c.nr_interno_conta 
and	c.nr_seq_retorno		= nr_seq_retorno_p;


BEGIN 
 
open c01;
loop 
fetch c01 into 
	nr_seq_procedimento_w, 
	nr_seq_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	 
	if (nr_seq_procedimento_w	> 0) then 
		CALL reatualizar_proc_rep_retorno(nr_seq_procedimento_w, nr_seq_retorno_p, nm_usuario_p);
	elsif (nr_seq_material_w	> 0) then 
		CALL reatualizar_mat_rep_retorno(nr_seq_material_w, nr_seq_retorno_p, nm_usuario_p);
	end if;
end loop;
close c01;
 
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
