SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE recalcular_venc_repasse (nr_seq_rep_venc_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_tributo_w		repasse_terc_venc_trib.vl_imposto%type;
vl_desc_tributo_w	repasse_terc_venc_trib.vl_imposto%type;
cd_tributo_w		repasse_terc_venc_trib.cd_tributo%type;

C01 CURSOR FOR
SELECT	distinct cd_tributo
from	repasse_terc_venc_trib
where	nr_seq_rep_venc		= nr_seq_rep_venc_p;


BEGIN

vl_desc_tributo_w	:= 0;

open C01;
loop
fetch C01 into
	cd_tributo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	coalesce(sum(vl_imposto),0)
	into STRICT	vl_tributo_w
	from	repasse_terc_venc_trib
	where	nr_seq_rep_venc		= nr_seq_rep_venc_p
	and		cd_tributo			= cd_tributo_w
	and		ie_pago_prev		in ('V','L');

	select	coalesce(sum(CASE WHEN a.ie_soma_diminui='S' THEN (coalesce(vl_tributo_w,0) * - 1) WHEN a.ie_soma_diminui='D' THEN  coalesce(vl_tributo_w,0)  ELSE 0 END ),0)
	into STRICT	vl_tributo_w
	from	tributo a
	where	a.cd_tributo		= cd_tributo_w;

	vl_desc_tributo_w	:= vl_desc_tributo_w + vl_tributo_w;

	end;
end loop;
close C01;

update	repasse_terceiro_venc
set		vl_liquido		= vl_vencimento - vl_desc_tributo_w,
		nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_rep_venc_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
