CREATE OR REPLACE PROCEDURE recalcula_dose_onc_ciclo ( nr_seq_paciente_p bigint, nm_usuario_p text, nr_ciclo_p bigint default 0) AS $body$
DECLARE

nr_seq_atendimento_w  bigint;

c01 CURSOR FOR
  SELECT  nr_seq_atendimento
  from  paciente_atendimento
  where  coalesce(nr_prescricao::text, '') = ''
  and  nr_seq_paciente = nr_seq_paciente_p
  and coalesce(dt_cancelamento::text, '') = ''
  and coalesce(dt_liberacao_reg::text, '') = ''
  and  ((coalesce(nr_ciclo_p,0) = 0) or (nr_ciclo_p = nr_ciclo));


BEGIN

open C01;
loop
fetch C01 into
  nr_seq_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
  begin
  CALL atualizar_dose_onc_ciclo(nr_seq_atendimento_w);
  end;
end loop;
close C01;

CALL recalcula_dose_onc_receita(nr_seq_paciente_p, nm_usuario_p, nr_ciclo_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
