CREATE OR REPLACE PROCEDURE recalcula_dose_prescr_ciclo ( nr_seq_paciente_p bigint, nr_seq_material_p bigint, qt_percentual_p bigint, ie_tipo_percentual_p text, nm_usuario_p text, ds_dia_ciclo_p text, nr_ciclo_p bigint) AS $body$
DECLARE

nr_seq_atendimento_w	bigint;

c01 CURSOR FOR
	SELECT	nr_seq_atendimento
	from	paciente_atendimento
	where	coalesce(nr_prescricao::text, '') = ''
	and		nr_seq_paciente = nr_seq_paciente_p
	and		trunc(coalesce(dt_real,dt_prevista)) >= trunc(clock_timestamp())
	and		coalesce(nr_prescricao::text, '') = ''
	and		coalesce(dt_liberacao_reg::text, '') = ''
	and		coalesce(dt_suspensao::text, '') = ''
	and		((nr_ciclo > nr_ciclo_p) or
			((somente_numero(ds_dia_ciclo) > somente_numero(ds_dia_ciclo_p)) and (nr_ciclo = nr_ciclo_p)));


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (ie_tipo_percentual_p = '1') then

		update	paciente_atend_medic
		set     qt_dose_prescricao = (qt_dose_prescricao + ((qt_percentual_p * coalesce(qt_dose_prescricao,0)) / 100)),
				ie_tipo_percentual = ie_tipo_percentual_p
		where   nr_seq_atendimento = nr_seq_atendimento_w
		and     nr_seq_material    = nr_seq_material_p;

	else

		update	paciente_atend_medic
		set     qt_dose_prescricao = (qt_dose_prescricao - ((qt_percentual_p * coalesce(qt_dose_prescricao,0)) / 100)),
				ie_tipo_percentual = ie_tipo_percentual_p
		where   nr_seq_atendimento = nr_seq_atendimento_w
		and     nr_seq_material    = nr_seq_material_p;

	end if;

	commit;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
