CREATE OR REPLACE PROCEDURE recalc_curva_abc_item_segmento ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_curva_abc_w		varchar(1);
cd_material_w		bigint;
nr_seq_segmento_w	bigint;


BEGIN

select	cd_material,
	nr_seq_segmento
into STRICT	cd_material_w,
	nr_seq_segmento_w
from	nc_curva_abc_item
where	nr_sequencia = nr_sequencia_p;


ie_curva_abc_w := obter_curva_abc_item_segmento(clock_timestamp(), nr_seq_segmento_w, cd_material_w, ie_curva_abc_w);

update	nc_curva_abc_item
set	ie_curva_item	= ie_curva_abc_w,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_sequencia_p;

commit;

end;
