CREATE OR REPLACE PROCEDURE receber_emprestimo_pertence ( nr_seq_registro_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE




nr_sequencia_w		bigint;
cd_setor_atendimento_w	integer;
qt_item_w		bigint;


BEGIN

select 	qt_item
into STRICT	qt_item_w
from	pertence_paciente_item
where	nr_sequencia	=	nr_sequencia_p
and	nr_seq_pertence_paciente = nr_seq_registro_p;

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	pertence_pac_item_evento
where	nr_seq_pert_pac = nr_sequencia_p
and	cd_tipo_evento	= 'S';

select	cd_setor_atendimento
into STRICT	cd_setor_atendimento_w
from	pertence_pac_item_evento
where	nr_sequencia = nr_sequencia_w;

insert into	pertence_pac_item_evento(
				nr_sequencia,
				nr_seq_pert_pac,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				cd_tipo_evento,
				qt_transacao,
				cd_setor_atendimento)
			values (
				nextval('pertence_pac_item_evento_seq'),
				nr_sequencia_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				'D',
				qt_item_w,
				cd_setor_atendimento_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
